<script>
var Gallery = function Gallery(slider,eq){
var self = this;
this.destroy = function(){
self.overlay.removeClass("show");
setTimeout(function(){ self.overlay.remove(); },500);
return true;
};

this.overlay = $("<div></div>").addClass("overlay").click(function(e){ e.stopPropagation(); self.destroy(); });
this.wrapper = $("<div></div>").addClass("wrapper");
this.slider =  $("<div></div>").addClass("slider");
this.slider_nav =  $("<ul></ul>").addClass("slider_nav").click(function(e){ e.stopPropagation(); });

this.overlay.append(this.wrapper.append(this.slider,this.slider_nav));

this.createVideo = function(elem){
return new VPlayer(elem);
};
this.createGif = function(elem){
return new VPlayer(elem,true,false,false);
};
this.createImg = function(elem){ return $("<img>").attr('alt',"Obrazek").attr("src",elem.attr("data-src"));};

this.changeSldie = function(eq){
var old = self.slider.find(".current");
if(self.slider.find(".slide").index(old) !== eq){
if(old.hasClass("video" || "gif")){
if(old.children("video")){
old.children("video").get(0).pause();
if(old.children("video").get(0).load){old.children("video").get(0).load();}
}

old.removeClass("started");
}
}
var current = self.slider.find(".slide").eq(eq);

if(current.hasClass("video")){
if(current.find("video").length===0){
current.append(self.createVideo(current));
} else {
current.find("video").get(0).pause();
if(current.find("video").get(0).load){current.find("video").get(0).load();}
}
} else if(current.hasClass("gif")){
if(current.find("video").length===0){
current.append(self.createGif(current));
current.find("video").get(0).play();
} else {
current.find("video").get(0).play();
}
} else {
if(current.filter("img").length === 0){
current.append(self.createImg(current));
}
}
self.wrapper.height(current.height() + self.slider_nav.height());
old.removeClass("current");
current.addClass("current");

self.slider_nav.find(".current").removeClass("current");
self.slider_nav.find("li").eq(eq).addClass("current");
};


this.content = function(){
slider.find(".slide").each(function(){
self.slider.append($("<div></div>")
.addClass($(this).attr("class"))
.attr("data-title", $(this).attr("data-title"))
.attr("data-src", $(this).attr("data-src"))
.attr("data-src2", $(this).attr("data-src2")).on("click","*",function(e){e.stopPropagation();}));
self.slider_nav.append($("<li></li>"));
});

self.slider_nav.find("li").click(function(e){
self.changeSldie($(this).parent().find("li").index(this));
});
};

$("body").append(this.overlay);
setTimeout(function(){
self.content();
self.changeSldie(eq);
self.overlay.addClass("show");
},0);

};

var VPlayer = function VPlayer(elem,auto,ctrl,count){
if(typeof auto === "undefined"){auto = false;}
if(typeof ctrl === "undefined"){ctrl = true;}
if(typeof count === "undefined"){count = true;}

var el = document.createElement("video");
$(el).append($("<source>").attr("src",elem.attr("data-src")));
$(el).append($("<source>").attr("src",elem.attr("data-src2")));
el.autostart = auto;
el.controls = ctrl;
if(el.load){el.load();}

if(count){
var sStart = sFirst = sHalf = sThird = sEnd = 0;
$(el).on('playing play', function() { if (! sStart++) { SamsungDataLayer.push({ 'event': 'gaevent', 'eventCategory': 'Video', 'eventAction': 'AddWash - '+elem.attr("data-title"), 'eventLabel': 'Play' }); } });  // Start
$(el).on('timeupdate', function() {
if (el.currentTime >= .25 * el.duration && !sFirst++) { SamsungDataLayer.push({ 'event': 'gaevent', 'eventCategory': 'Video', 'eventAction': 'AddWash - '+elem.attr("data-title"), 'eventLabel': '25%' }); }
if (el.currentTime >= .5 * el.duration && !sHalf++)   { SamsungDataLayer.push({ 'event': 'gaevent', 'eventCategory': 'Video', 'eventAction': 'AddWash - '+elem.attr("data-title"), 'eventLabel': '50%' }); }
if (el.currentTime >= .75 * el.duration && !sThird++) { SamsungDataLayer.push({ 'event': 'gaevent', 'eventCategory': 'Video', 'eventAction': 'AddWash - '+elem.attr("data-title"), 'eventLabel': '75%' }); }
if (el.currentTime >= .95 * el.duration && !sEnd++)   { SamsungDataLayer.push({ 'event': 'gaevent', 'eventCategory': 'Video', 'eventAction': 'AddWash - '+elem.attr("data-title"), 'eventLabel': '100%' }); }
});
}
return $(el);
};

