$(document).ready(function() {
$(".loader").fadeOut(500);
var Wys = $(window).height();
$('.cmno-slide').css('min-height', (Wys-170) + 'px');


});

$(window).resize(function() {
var Wys = $(window).height();
$('.cmno-slide').css('min-height', (Wys-170) + 'px');
});

$(window).scroll(function(){
var scrollY = Math.round($(window).scrollTop() - $('body').offset().top);
var Wys = $(window).height() - 170;
var speed = Math.round(scrollY / 1);
//console.log(speed);
if (speed > 50) { $('.unia2').fadeOut(1000); }
if (speed < 50) { $('.unia2').fadeIn(1000); }
if (speed < 200) { $('.cmno-slide').css('min-height', Wys-speed+'px'); }
var stopListener = $(window).mouseup(function(){ // listen to mouse up
stopListener();
});
if (speed > 50) {
$('.site_languages').css('margin-top','-22px');
$('.a100').css('margin-top','1px');

}
if (speed < 50) {
$('.site_languages').css('margin-top','0px');
$('.a100').css('margin-top','25px');

}
});

</script>
</body>
</html>
