<script>var width = document.body.clientWidth;

if(width >= 1024)
var clone_menu = $("#standard_menu").clone().appendTo('body');
else
var clone_menu = $("#mobile_menu").clone().appendTo('body');

var menu = $(".menu");
var kotwice = $('.kotwica');
var elementy = $('.nav_standard');
var offset = $("#standard_menu").offset().top;

var required_width = 955;
$(clone_menu).hide();
$(clone_menu).css('position', 'fixed');
$(clone_menu).css('z-index', 1000);
$(clone_menu).css('top', 0);
$(clone_menu).find('ul li').css('padding-top', '10px');

if(width <= 1023){
required_width = 250;
offset = $("#mobile_menu").offset().top;
}

/*--------- Przyklejane menu -------*/
$(document).scroll(function(){

var y = $(document).scrollTop();

if(y > offset){
$(clone_menu).slideDown('fast');
}else{
$(clone_menu).slideUp('fast');
}

var name = "start";

kotwice.each(function(){

if(y + 60 > $(this).offset().top){
name = $(this).attr('id');
return;
}

});

elementy.each(function(){

if($(this).attr('href') == "#" + name){

if(y < required_width){
$(".curr").text("Menu");
}else{
$(".curr").text($(this).text());
}

$(this).addClass('active');
}else{
$(this).removeClass('active');
}

});

});

$("#more_head_button").on('click', function(){
var target = $(this).attr('href');
$('html, body').animate({scrollTop: $(target).offset().top}, 1000);
return false;
});

$(document).ready(function(){
$(document).bind("contextmenu",function(e){
return false;
});
});</script>
