function check() {
if ($(document).width() > 1000) {
var body_h = $('body').height();
body_h = parseInt(body_h);
body_h = body_h * 8 / 100;
var f_size = body_h * 25 /100;
if (body_h > 80) body_h = 80;
$('.partnerlogos').css('height', body_h);
$('#galeriaw span').css('font-size', f_size);
}
}

check();
$(window).ready(function() { setTimeout(function() { check(); }, 450); });
$(window).resize(function() { setTimeout(function() { check(); }, 450); });



$(window).scroll(function() {

var g_top = $('#g_top').outerHeight();
var kbg = $('.kbg').outerHeight();
var wspl = $('#wspl').outerHeight();
var ooo = $('.ooo').outerHeight();
var documentHeight = document.documentElement.clientHeight;

g_top = parseInt(g_top);
kbg = parseInt(kbg);
wspl = parseInt(wspl);
//wspl = 2 * wspl;
ooo = parseInt(ooo);
//var all = kbg + g_top - wspl - ooo;
var all = kbg + g_top + wspl - documentHeight;
//alert(all);


if ($(document).scrollTop() > all) {
$('#wspl').css('position', 'static');
}

if ($(document).scrollTop() <= all) {
$('#wspl').css('position', 'fixed');

}
});

</script>

