}

var zakladkiKierunek = 'right';
var zakladkiSzerokosc = 377;
var zakladkiWidoczne = 89;
var zakladkiNieWidoczne = zakladkiSzerokosc - zakladkiWidoczne;

function showZakladka(czas, hide)
{
if (zakladkiKierunek == 'left')
$('#zakladki').stop(true, false).animate({left: "0"}, czas, function()
{
if (hide)
setTimeout('hideZakladka(' + czas + ')', 1000);
});
if (zakladkiKierunek == 'right')
$('#zakladki').stop(true, false).animate({right: "0"}, czas, function()
{
if (hide)
setTimeout('hideZakladka(' + czas + ')', 1000);
});
}

