lution({        sliderType:"standard",        sliderLayout:"auto",        delay:5000,        navigation: {            arrows:{enable:true}        },        gridwidth:1230,        gridheight:687    });    revapi.bind("revolution.slide.onchange",function (e,data) {        $('.banner_btn').removeClass('current_slide');        if(data.slideIndex==1){            $('#first_banner_btn').addClass('current_slide');        }        else if(data.slideIndex==2){            $('#second_banner_btn').addClass('current_slide');        }        else if(data.slideIndex==3){            $('#third_banner_btn').addClass('current_slide');        }    });    $('.banner_btn').click(function(e){        $('.banner_btn').removeClass('current_slide');        revapi.revshowslide($(this).data('slide'));        $(this).addClass('current_slide');    });});$('#counting_people').waypoint(function() {    if( typeof $(this).data('done') == 'undefined'){        var element_options = {            useEasing: false,            useGrouping: false,            separator: '',            decimal: ',',            prefix: '',            suffix: ''        }        if ($(this).data('number') % 1 != 0) {            decimal = 1;        }        else {            decimal = 0;        }        var element_name = new countUp(this, 0, $(this).data('number'), decimal, 1.5, element_options);        element_name.start();        $(this).data('done', true);    }}, { offset: 'bottom-in-view' });var mobileViewport = window.matchMedia("screen and (max-width: 767px)");var doubleBanner =$('#double_banner');if(!mobileViewport.matches) {    doubleBanner.waypoint(function() {        $('#double_banner_circle').addClass('animated');    }, {offset: '0%'});    doubleBanner.waypoint(function() {        $('.double_banner_txt').addClass('animated');    }, {offset: '-20%'});}function reset_html(id) {    $('#'+id).