<script type="text/javascript">

// Animacja gornego menu oraz animacje po przewinieciu
var lastId, topMenu, bottomMenu, topMenuHeight, menuItems, scrollItems, menuItemsFooter, scrollItemsFooter;
$(function() {
topMenu = $("#ul_nawigacja");
bottomMenu = $("#ul_nawigacjaFooter");
topMenuHeight = topMenu.outerHeight() + 65;
// Wszystkie elementy listy
menuItems = topMenu.find("a");
// Kotwice do pozycji menu
scrollItems = menuItems.map(function () {
var item = $($(this).attr("href"));
if (item.length) { return item; }
});
// Wszystkie elementy listy
menuItemsFooter = bottomMenu.find("a");
// Kotwice do pozycji menu
scrollItemsFooter = menuItemsFooter.map(function () {
var item = $($(this).attr("href"));
if (item.length) { return item; }
});

// Animacje po kliknieciu w pozycje menu
menuItems.click(function (e) {
var href = $(this).attr("href"),
offsetTop = href === "#" ? 0 : $(href).offset().top - topMenuHeight + 116;
$('html, body').stop().animate({
scrollTop: offsetTop
}, 1000);
e.preventDefault();
});
menuItemsFooter.click(function (e) {
var href = $(this).attr("href"),
offsetTop = href === "#" ? 0 : $(href).offset().top - topMenuHeight + 116;
$('html, body').stop().animate({
scrollTop: offsetTop
}, 1000);
e.preventDefault();
});

// Po przewinieciu
$(window).scroll(function () {
// Pobierz pozycje kontenera
var fromTop = $(this).scrollTop() + topMenuHeight;

// Pobierz identyfikator aktualnej pozycji przewiniecia
var cur = scrollItems.map(function () {
if ($(this).offset().top < fromTop)
return this;
});
// Pobierz identyfikator aktualnego elementu
cur = cur[cur.length - 1];
var id = cur && cur.length ? cur[0].id : "";

if (lastId !== id) {
lastId = id;
// Ustaw lub usun "active" - odpowiada za podswietlenie pozycji w menu
menuItems
.parent().removeClass("active")
.end().filter("[href=#" + id + "]").parent().addClass("active");
}
});
});
</script>

