<script type="text/javascript">
/*<![CDATA[*/

$j(document).ready(function(){

if($j.cookie('cookie_accept')==1){
$j(".cookiePolicy").remove();
}

$j(".jqAcceptCookie").click(function(){
var date = new Date();
date.setTime(date.getTime()+(180*24*60*60*1000));
var expires = "; expires="+date.toGMTString();
document.cookie = "cookie_accept=1"+expires+"; path=/";
$j(".cookiePolicy").remove();
});



var cookieSrcImg = "/themes/Default/css/controls/images/cookie_header.png";

if($j("body").hasClass("DE")){
$j(".cookiePolicy .content").html("<strong>Cookies auf der Seite</strong> Cookies helfen uns bei der Bereitstellung unserer Dienste. Durch die Nutzung unserer Dienste erklaren Sie sich damit einverstanden, dass wir Cookies setzen.");
$j(".cookiePolicy .accept_text").html("OK");
}else if($j("body").hasClass("EN")){
$j(".cookiePolicy .content").html("<strong>Cookies</strong> Cookies help us deliver our services. By using our services, you agree to our use of cookies.");
$j(".cookiePolicy .accept_text").html("OK");
}else if($j("body").hasClass("RU")){
$j(".cookiePolicy .content").html("<strong>politika konfidentsial'nosti i informatsiia o failakh cookie</strong> Cookie pomoch' nam dostavit' nashi uslugi. Pol'zuias' nashimi uslugami, Vy soglashaetes' s nashimi ispol'zovaniia cookie.");
$j(".cookiePolicy .accept_text").html("v poriadke");
}else{
$j(".cookiePolicy .content img").attr("src",cookieSrcImg);
}



});


/*]]>*/
</script>


