var ajaxCaller=null;$(function(){var form=$('#login-panel');form.submit(SubmitForm).find('dd>*').change(ValidateForm).keyup(function(){ClearFieldError($(this));});ajaxCaller=new HypAjaxAction({holder:form,errorTranslate:function(err){if(err.key.indexOf('[')>=0){if(err.value==2)return 'Nieprawidlowa wartosc';return 'unknown error';}return 'send error';},onSuccess:function(form,result){$('dd>*',form).val('');},onFailure:function(form,result){result.errors.forEach(function(err){var fieldName=err.key.replace('message[','').replace(']','');MarkFieldError($('dd>#'+fieldName,form));});return false;}});});function SubmitForm(event){if(ValidateForm(event)){ajaxCaller.SaveForm();}return false;}function ClearFieldError(field){var parent=field.parent();if(parent.hasClass('error'))parent.removeClass('error').parent().find('span').removeClass('field-validation-error').addClass('field-validation-valid');}function MarkFieldError(field){field.parent().addClass('error').parent().find('span').addClass('field-validation-error').removeClass('field-validation-valid');}function ValidateForm(event){var target=event.target;var result=true;if(target.form===undefined){form=$(target);result=ValidateEmail($('dd>#from',form));result=ValidateMessage($('dd>#text',form))&& result;}else{if(target.id==='from'){ValidateEmail($(target));}else{ValidateMessage($(target));}}return result;function ValidateEmail(field){if(!field.val().match(/^[\w\d_\-\.]+@([\w\d_\-]+\.)+[a-zA-Z]{2,5}$/)){MarkFieldError(field);return false;}ClearFieldError(field);return true;}function ValidateMessage(field){if(field.val().length < 6){MarkFieldError(field);return false;}ClearFieldError(field);return true;}}
//]]>
</script>
<script src="http://connect.facebook.net/pl-PL/sdk.js#xfbml=1&amp;version=v2.0" type="text/javascript" charset="UTF-8"></script>
</body></html>
