<script type="text/javascript">
var map = null;
var gdealers	= [];
var Ids			= [];
var Names		= [];
var Addrs		= [];
var RRDIIds		= [];
var Citys		= [];
var Htmls		= [];
var bounds = null;
var infoWindow = null;
var markers = [];
var iSelectedMarker = -1;


var mapId = 'mediumMapAgentsList';

map_load();

function map_load()
{
var mapOptions = null;

switch ('')
{
case "fixed":
mapOptions = getFixedMapOption();
break;
default:

mapOptions = getDefaultMapOption();
break;
}

map = new google.maps.Map(document.getElementById(mapId), mapOptions);

displayDealersCallBack(map);

var markerCluster = new MarkerClusterer(map, markers);
}

function getFixedMapOption() {
var mapOptions = {
center: new google.maps.LatLng('19.55', '50.03'),
zoom: 10,
mapTypeId: google.maps.MapTypeId.ROADMAP,
scrollwheel: false,
navigationControl: false,
mapTypeControl: false,
scaleControl: false,
draggable: false,
streetViewControl: false,
disableDoubleClickZoom : true
};
return mapOptions;
}

function getDefaultMapOption() {
var mapOptions = {
center: new google.maps.LatLng('19.55', '50.03'),
zoom: 10,
mapTypeId: google.maps.MapTypeId.ROADMAP

};
return mapOptions;
}

function displayDealersCallBack(map)
{

var dealers = htmlDecode("&lt;Dealers&gt;&lt;Dealer name=&quot;RIA GRUDZIADZ&quot; CODE_RDI=&quot;PL5044P&quot; Addr=&quot;PADEREWSKIEGO 183A#br#86-300 GRUDZIADZ&quot; PHONENUMBER=&quot;56 640 01 08&quot; X_COORDINATE=&quot;18.820362&quot; Y_COORDINATE=&quot;53.505903&quot; DISTANCE=&quot;&quot; URLHOME=&quot;https://dealer.peugeot.pl/ria.grudziadz&quot; URLINFO=&quot;&quot; ACTIVATESITE=&quot;True&quot; EDEALERORAGENT=&quot;False&quot; ISAGENT=&quot;False&quot; IDGEOSITE=&quot;0000046627&quot; /&gt;&lt;Dealer name=&quot;RIA JAMNICA&quot; CODE_RDI=&quot;PL4204P&quot; Addr=&quot;Jamnica 160#br#39-410 Stalowa Wola / Greb&#243;w&quot; PHONENUMBER=&quot;0158113240&quot; X_COORDINATE=&quot;21.9264565&quot; Y_COORDINATE=&quot;50.5797966&quot; DISTANCE=&quot;&quot; URLHOME=&quot;https://dealer.peugeot.pl/ria.stalowawola&quot; URLINFO=&quot;&quot; ACTIVATESITE=&quot;True&quot; EDEALERORAGENT=&quot;False&quot; ISAGENT=&quot;False&quot; IDGEOSITE=&quot;0000042785&quot; /&gt;&lt;/Dealers&gt;");

bounds = new google.maps.LatLngBounds();
gdealers	= [];
numDealer = 0;

$("<div>" + dealers + "</div>").find('Dealer').each(function() {

var id = $(this).attr("PHONENUMBER");
var rrdiid = $(this).attr("CODE_RDI");
var addr = $(this).attr("Addr");
var name = $(this).attr("name");
var lat = parseFloat($(this).attr("Y_COORDINATE"));
var lng = parseFloat($(this).attr("X_COORDINATE"));
var home = $(this).attr("URLHOME");
var infos = $(this).attr("URLINFO");
var isAgent = $(this).attr("ISAGENT").match(/^true$/i);
var point = new google.maps.LatLng(lat, lng);

CreateDealer(id, name, addr, rrdiid, point, home, infos, isAgent, map, numDealer);

bounds.extend(point);

numDealer++;
});

if(numDealer >= 1 ){
map.fitBounds(bounds);
}
var zoomLevel = 12;
if (zoomLevel > 0 && numDealer == 1 ) {

setZoomLevel(map, zoomLevel);
}
}

function setZoomLevel(map, zoomLevel) {
zoomChangeBoundsListener =
google.maps.event.addListenerOnce(map, 'bounds_changed', function(event) {
if (this.getZoom()) {
this.setZoom(zoomLevel);
}
});
setTimeout(function() { google.maps.event.removeListener(zoomChangeBoundsListener) }, 2000);
}

function resizeMap(){
google.maps.event.trigger(map, 'resize');
map.fitBounds(bounds);
}



function CreateDealer(id, name, addr, rrdiid, point, home, infos, isAgent, map, numDealer) {

///=== Creation de l'icone

var isSmall = ('True' == 'True');
var markerImage = getGmapPicto();

///===Informations Dealer
Ids[numDealer] = id;
Names[numDealer] = name;
Addrs[numDealer] = addr;
RRDIIds[numDealer] = rrdiid;

var html = "";

html = "<div class='map-content'>" +
'<h3>' + name + '</h3>' +
'<br />' +
addr.replace(/\#br#/g, '<br />') +
'';
if ('False' == 'True') {
html += '<br /><p class="icon-phone">' +
'<big><strong>' + id + '</strong></big>' +
'</p>';
}

if ('False' == 'True') {



var URLMustStartWith = 'http://';
var URLMustStartWith2 = 'https://';
var regex1 = new RegExp("^" + URLMustStartWith + "");
var regex2 = new RegExp("^" + URLMustStartWith2 + "");
if (!regex1.test(home) && !regex2.test(home)){

home='//'+home;
}





html += '<br /><p>' +
'<a href="' +  home + '"class="btn-link">' + "PRZEJDZ NA STRONE INTERNETOWA" +'</a>' +
'</p>';
}

html += '</div>';

Htmls[numDealer] = html;
gdealers[numDealer] = marker;

var marker = new google.maps.Marker({
position: point,
map: map,
icon: markerImage
});

//Utilise sur la page des agents pour retrouver le marker correspondant au pdv
if(mapId == 'bigMapAgentsList' || mapId == 'grp_map'){
markers.push(marker);
}

if ('' != "fixed") {

google.maps.event.addListener(marker, 'click', function() {

if (infoWindow) {
infoWindow.close();
if(iSelectedMarker != -1 && markers[iSelectedMarker]!=null){
markers[iSelectedMarker].setIcon(getGmapPicto());
}

}
map.setZoom(12);
map.setCenter(marker.getPosition());

iSelectedMarker = numDealer;
marker.setIcon(getGmapPicto());
infoWindow = new google.maps.InfoWindow({ content: html });
infoWindow.open(map, marker);

//focus dealer (traitement specifique a la page grpe)
if( mapId == 'grp_map'){
focusGrpListDealer(numDealer);
}
});
}

marker.setMap(map);
}

function getGmapPicto(){

var peugeotURL = '/Styles/HTML5/img/common/gmaps/pin-off.png';

return new google.maps.MarkerImage(peugeotURL);

}
function htmlDecode(value){
return $('<div/>').html(value).text();
}

var isLocked = false;
function openInfoWindow(index){

if (typeof markers != 'undefined' && !isLocked) {
google.maps.event.trigger(markers[index], 'click');
}
}

function closeInfoWindow(){

if (typeof markers != 'undefined') {
if (infoWindow) {
infoWindow.close();
}

for(var i=0; i<markers.length;i++){
markers[i].setIcon(getGmapPicto());
}
resizeMap();
}
}

function focusGrpListDealer(numDealer){
isLocked = true;
var listElem = $('.dealerName').filter(function() {
return $(this).text().indexOf(numDealer+1+".") == 0;
});

if (typeof listElem != 'undefined') {

listElem.parent().click();

}
isLocked = false;
}

</script>


