<script type="text/javascript">
(function($) {

$.fn.menumaker = function(options) {

var cssmenu = $(this), settings = $.extend({
title: "Menu",
format: "dropdown",
sticky: false
}, options);


return this.each(function() {
cssmenu.prepend('<div id="menu-button" style="text-align: right;"><div style="display: inline-block; float: left; font-size: 20px; line-height: 54px;">' + settings.title + '</div><div class="mobile-nav hamburger hamburger--collapse"><div style="display: inline-block;text-align: right"><div class="hamburger-box"><div class="hamburger-inner"></div></div></div></div></div>');
$(this).find("#menu-button").on('click', function(){
$(this).toggleClass('menu-opened is-active');
$('.mobile-nav').toggleClass('is-active');
var mainmenu = $(this).next('ul');
if (mainmenu.hasClass('open')) {
mainmenu.hide().removeClass('open');
}
else {
mainmenu.show().addClass('open');
if (settings.format === "dropdown") {
mainmenu.find('ul').show();
}
}
});

cssmenu.find('li ul').parent().addClass('has-sub');

multiTg = function() {
cssmenu.find(".has-sub").prepend('<span class="submenu-button"></span>');
cssmenu.find('.submenu-button').on('click', function() {
$(this).toggleClass('submenu-opened');
if ($(this).siblings('ul').hasClass('open')) {
$(this).siblings('ul').removeClass('open').hide();
}
else {
$(this).siblings('ul').addClass('open').show();
}
});
};

if (settings.format === 'multitoggle') multiTg();
else cssmenu.addClass('dropdown');

if (settings.sticky === true) cssmenu.css('position', 'fixed');

resizeFix = function() {
if ($( window ).width() > 768) {
cssmenu.find('ul').show();
}

if ($(window).width() <= 768) {
cssmenu.find('ul').hide().removeClass('open');
}
};
//resizeFix();
//return $(window).on('resize', resizeFix);

});
};
})(jQuery);

