<script>
$(function(){
$("select[name=form_rodzaj]").change(function(){
$("section.component.offers_search section.form form").removeClass();
$("section.component.offers_search section.form form").addClass($(this).val());
});
$("select[name=form_lokalizacja]").change(function(){
$("section.component.offers_search section.form form label.dzielnica").removeClass("display");
if ($(this).val() == "dzielnice") $("section.component.offers_search section.form form label.dzielnica").addClass("display");
});
$("input[name=form_miejscowosc]").keyup(function(){
miejscowosc_select($(this).val());
});
$("input[name=form_miejscowosc]").select(function(){
miejscowosc_select($(this).val());
});
$("input[name=form_miejscowosc]").change(function(){
miejscowosc_select($(this).val());
});
});
function miejscowosc_select(miejscowosc){
if (miejscowosc.toLowerCase() == "szczecin") $("select[name=form_lokalizacja]").prop("disabled", false);
else
{
$("select[name=form_lokalizacja]").val("");
$("section.component.offers_search section.form form label.dzielnica").removeClass("display");
$("select[name=form_lokalizacja]").prop("disabled", true);
}
}

function openOffersDialog()
{
$("#pg_overlay").fadeIn('fast', function()
{
$("#boxpopup").css('display','block');
$("#boxpopup").css({'top': '-130px'});
$("#boxpopup").animate({'top': '50%'},500);
});
}
function closeOffersDialog(prospectElementID)
{
$(function($)
{
$(document).ready(function()
{
$("#" + prospectElementID).animate({'top':'-130px'}, 500, function()
{
$("#" + prospectElementID).css('top','-130px');
$("#pg_overlay").fadeOut('fast');
});
});
});
}
$("#pg_overlay").click(function(){
closeOffersDialog('boxpopup');
});
$(document).ready(function()
{
openOffersDialog();
});
$(function(){
$("div#cookies_info a.close").click(function(){
document.cookie= 'cookies_info=1; path=/';
$("div#cookies_info").hide('blind');
});
});
