kodPocztowyChanged: function()
{
var val = $(this).val();
while(0 < val.search('-'))
{
val = val.replace('-','');
}
if(2 > val.length)
{
$(this).val(val);
return;
}
var valNew = val.substr(0, 2) + '-';
valNew += val.substr(2, 3);
$(this).val(valNew);
},
