center: [51.005236, 17.1717],
minZoom: 2,
zoom: 10
});

L.tileLayer( 'https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {attribution: '&copy; <a href="https://www.openstreetmap.org/copyright" rel="nofollow">OpenStreetMap</a>', subdomains: ['a','b','c']} ).addTo( map );

var myIcon = L.icon({
iconUrl: 'https://pportale.pl/img/ico_google_maps.png',
iconRetinaUrl: 'https://pportale.pl/img/ico_google_maps.png',
iconSize: [32, 32],
iconAnchor: [9, 21],
popupAnchor: [0, -14]
});

var markerClusters = L.markerClusterGroup();

for ( var i = 0; i < locations.length; ++i )
{
var markerBox = '<div class="mapIconBox">';
if (locations[i].z != '') {markerBox += '<img src="' + locations[i].z + '" alt="' + locations[i].n + '">';}
markerBox += '<br><a href="' + locations[i].u + '" title="' + locations[i].n + '" target="_blank">' + locations[i].n + '</a>';
markerBox += '<br><small>' + locations[i].a + '</small>';
markerBox += '</div>';

var markers = L.marker( [locations[i].lat, locations[i].lng], {icon: myIcon} ).bindPopup( markerBox );
markerClusters.addLayer( markers );
}

function cooridinate()
{
document.getElementById('siteDate').dataset.zoom = map.getZoom();
document.getElementById('siteDate').dataset.center = map.getCenter();
}

window.onload = function()
{
document.getElementById('siteDate').onclick = function() {cooridinate();};
}

map.addLayer( markerClusters );
