<script>
document.addEventListener('DOMContentLoaded', function(event) {
var countDownDate1 = new Date('Nov 22, 2018 13:20:00 GMT+0100').getTime();
var countDownDate2 = new Date('Jan 04, 2019 17:00:00 GMT+0100').getTime();
function srvTime(url, callback) {
var xmlHttp;
try {
xmlHttp = new XMLHttpRequest();
}
catch (err1) {
try {
xmlHttp = new ActiveXObject('Msxml2.XMLHTTP');
}
catch (err2) {
xmlHttp = new ActiveXObject('Microsoft.XMLHTTP');
}
}
xmlHttp.open('HEAD', url, true);
xmlHttp.onreadystatechange = function() {
if (xmlHttp.readyState === 2) {
callback(xmlHttp.getResponseHeader('Date'));
}
};
xmlHttp.setRequestHeader('Content-Type', 'text/html');
xmlHttp.send('');
}

srvTime(window.location.href.toString(), makeTimer);

function makeTimer(st) {
var stMillis = Date.parse(st);
var lt = new Date();
var deltaTime = lt - stMillis;
var clock1 = document.querySelector('.clock1');
var daysSpan1 = clock1.querySelector('.days');
var hoursSpan1 = clock1.querySelector('.hours');
var minutesSpan1 = clock1.querySelector('.minutes');
var clock2 = document.querySelector('.clock2');
var daysSpan2 = clock2.querySelector('.days');
var hoursSpan2 = clock2.querySelector('.hours');
var minutesSpan2 = clock2.querySelector('.minutes');
var secondsSpan2 = clock2.querySelector('.seconds');
var startVotingInterval;
var endVotingInterval;

function setClockTitle(className) {
var clockTitle;
var label;
label = document.querySelector(className).dataset.label;
if (label) {
clockTitle = document.createElement('p');
clockTitle.classList.add('clockLabel');
clockTitle.innerText = label;
document.querySelector(className).parentNode.insertBefore(clockTitle, document.querySelector(className));
}
return;
}

function setClockLabels(clockContainer) {
document.querySelector(clockContainer + ' .js_days').innerHTML = document.querySelector(
clockContainer + ' .js_days').dataset.label;
document.querySelector(clockContainer + ' .js_hours').innerHTML = document.querySelector(
clockContainer + ' .js_hours').dataset.label;
document.querySelector(clockContainer + ' .js_minutes').innerHTML = document.querySelector(
clockContainer + ' .js_minutes').dataset.label;
document.querySelector(clockContainer + ' .js_seconds').innerHTML = document.querySelector(
clockContainer + ' .js_seconds').dataset.label;
}

setClockTitle('.js_timeToStart');
setClockTitle('.js_timeToFinish');
setClockLabels('.clockContainer1');
setClockLabels('.clockContainer2');

function getRemainingTime(deadline) {
var now = new Date().getTime() - deltaTime;
var distance = deadline - now;

return {
distance: distance,
days: Math.floor(distance / (1000 * 60 * 60 * 24)),
hours: Math.floor((distance / (1000 * 60 * 60)) % 24),
minutes: Math.floor((distance / 1000 / 60) % 60),
seconds: Math.floor((distance / 1000) % 60)
};
}

function updateTimer1() {
var distance = getRemainingTime(countDownDate1);

daysSpan1.innerHTML = distance.days < 10 ? '0' + distance.days : distance.days;
hoursSpan1.innerHTML = distance.hours < 10 ? '0' + distance.hours : distance.hours;
minutesSpan1.innerHTML = distance.minutes < 10 ? '0' + distance.minutes : distance.minutes;

if (distance.distance < 0) {
clearInterval(startVotingInterval);
document.querySelector('.clockContainer1').classList.add('inactive');
document.querySelector('.clockContainer2').classList.remove('inactive');
document.querySelector('.piWrap').classList.remove('piWrapDisabled');
}
else {
document.querySelector('.clockContainer1').classList.remove('inactive');
}
}

function updateTimer2() {
var distance2 = getRemainingTime(countDownDate2);

daysSpan2.innerHTML = distance2.days < 10 ? '0' + distance2.days : distance2.days;
hoursSpan2.innerHTML = distance2.hours < 10 ? '0' + distance2.hours : distance2.hours;
minutesSpan2.innerHTML = distance2.minutes < 10 ? '0' + distance2.minutes : distance2.minutes;
secondsSpan2.innerHTML = distance2.seconds < 10 ? '0' + distance2.seconds : distance2.seconds;

if (distance2.distance < 0) {
clearInterval(endVotingInterval);
document.querySelector('.clockContainer2').classList.add('inactive');
}
}

updateTimer1();
updateTimer2();
startVotingInterval = setInterval(updateTimer1, 1000);
endVotingInterval = setInterval(updateTimer2, 1000);
}
})
</script>
