window.galleryYouTubeQuery = '&vq=hd1080&hd=1';
/** /
loadinx();
var bgload = document.getElementById('bgload');
var text = bgload.innerHTML;
bgload.innerHTML = '';
for(var i=0; i<text.length; i++)
{
var ch = document.createElement('span');
ch.innerHTML = text.substr(i, 1);
if(ch.innerHTML == ' ')
ch.innerHTML = '&nbsp;&nbsp;';
bgload.appendChild(ch);
}
window.lodix = 0;
function loadinx()
{
window.lodix++;
var n = window.lodix;
var css = '';
var m = 10;
for(var i=0; i<m; i++)
{
var N = n%m;
var left = N;
var right = m-N;
var nn = Math.min(left, right);
var opacity = nn/m;
var opacity = (opacity*0.75) + 0.4;
var opacity = Math.min(1, opacity);
css += '#bgload span:nth-child('+m+'n'+(i==0?'':'-'+i)+') { opacity: '+opacity+'; }';
n++;
}
document.getElementById('loaderCSS').innerHTML = css;
}
window.loaderInterval = setInterval(function() { loadinx(); }, 200);
setTimeout(function()
{
clearInterval(window.loaderInterval);
}, 5000)
/**/
</script>
