document.addEventListener('DOMContentLoaded', function() {
var psgdpr_id_module = "16";
var parentForm = $('.gdpr_module_' + psgdpr_id_module).closest('form');

var toggleFormActive = function() {
var parentForm = $('.gdpr_module_' + psgdpr_id_module).closest('form');
var checkbox = $('#psgdpr_consent_checkbox_16');
var checkbox2 = $('#psgdpr_consent_checkbox_99');
var element = $('.gdpr_module_' + psgdpr_id_module);
var iLoopLimit = 0;

// Look for parent elements until we find a submit button, or reach a limit
while(0 === element.nextAll('[type="submit"]').length &&  // Is there any submit type ?
element.get(0) !== parentForm.get(0) &&  // the limit is the form
element.length &&
iLoopLimit != 1000) { // element must exit
element = element.parent();
iLoopLimit++;
}

if (checkbox.prop('checked') === true && checkbox2.prop('checked') === true) {
if (element.find('[type="submit"]').length > 0) {
element.find('[type="submit"]').removeAttr('disabled');
} else {
element.nextAll('[type="submit"]').removeAttr('disabled');
}
} else {
if (element.find('[type="submit"]').length > 0) {
element.find('[type="submit"]').attr('disabled', 'disabled');
} else {
element.nextAll('[type="submit"]').attr('disabled', 'disabled');
}
}
};

// Triggered on page loading
toggleFormActive();

// Listener ion the checkbox click
$(document).on('click' , '#psgdpr_consent_checkbox_16', function() {
toggleFormActive();
});
$(document).on('click' , '#psgdpr_consent_checkbox_99', function() {
toggleFormActive();
});
